import React, { Component } from 'react'
import ReactCardFlip from 'react-card-flip';
import SwipeCardFront from './SwipeCardFront';
import CardBack from './CardBack';


export default class SwipeCardContainer extends Component {

    constructor() {
        super();
          this.state = {
          isFlipped: false
        };
      }

      handleClick = e => {
        e.preventDefault();
        this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
      }

    render() {

        return (
            <ReactCardFlip isFlipped={this.state.isFlipped} flipDirection="horizontal">
                {document.body.style = 'background: white;'}
              <SwipeCardFront key="front" dog={this.props.dog} onClick={this.handleClick}>
              </SwipeCardFront>

              <CardBack key="back" dog={this.props.dog} onClick={this.handleClick}>

              </CardBack>
            </ReactCardFlip>
        )
    }
}
